import React, { useState, useEffect } from 'react'

const Header = () => {
  const [menusDown, setMenusDown] = useState(false);
  const [burgerMenu, setBurgerMenu] = useState(false)
  const getWindowDimensions = () => {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height
    };
  }
  const [ windowSize, setWindowSize] = useState(getWindowDimensions())

  useEffect(() => {
    const handleResize = () => {
      setWindowSize(getWindowDimensions());
    }
    window.addEventListener('resize', handleResize);
  }, [])


  const openMenu = () => {
    console.log("Clicked");
    if(burgerMenu === false) {
      setBurgerMenu(true)
    } else {
      setBurgerMenu(false)
    }
  }


  return (
    <header className="header">
        <div className="wrapper">
          <span className="logo">
            <img src="/assets/logo.svg" alt="Pitchers Logo"/>  
          </span>  
          <nav>
            <ul>
              <li><a href="/">Home</a></li>
              <li><a href="/">About</a></li>
              <li
                onMouseOver={() => setMenusDown(true)}
                onMouseOut={() => setMenusDown(false)}
              ><a href="/">Menus</a>
              { menusDown === true && 
                  <div className="menus-drop-down">
                    <div className="menu-triangle"></div>
                  <ul>
                    <li><a href="/">
                      <img src="/assets/menu/cutlery.svg" alt="Food"/>
                      <span>
                        <h2>Food</h2>
                        <p>Burgers, Pizza, Grill, vegetaraian and more.</p> 
                      </span>
                    </a></li>
                    <li><a href="/">
                    <img src="/assets/menu/cocktail.svg" alt="Drinks"/>
                    <span>
                      <h2>Drinks</h2>
                      <p>Beers, Cocktails, Spirits and more.</p>  
                    </span>                   
                      </a></li>
                  </ul>
                </div>
                }</li>
              <li className="contact-link">
                <span className="btn-bg"></span>
                <a href="/">Contact</a></li>
            </ul>
          </nav>
          <span className="burger-menu">
            <button onClick={() => openMenu()}>
                <img src="/assets/icons/burger-menu.svg" alt="Burger Menu"/>
            </button>
          </span>
          <nav className="responsive-menu" style={burgerMenu === true ? {height: "200px"} : { height: "0px"}} > 
            <ul>
              <li><a href="">Home</a></li>
              <li><a href="">About</a></li>
              <li><a href="">Menus</a></li>
              <li><a href="">Contact</a></li>
            </ul>
          </nav>
        </div>   
    </header>
  )
}
export default Header;
