  const changeImgLeft = () => {
    let newIndex = imgIndex - 1;
    if(newIndex < 1) {
      newIndex = 5;
      setImgIndex(newIndex);
    } else {
      setImgIndex(newIndex);
    }
  }
  const changeImgRight = () => {
    let newIndex = imgIndex + 1;
    if(newIndex > 5) {
      newIndex = 1;
      setImgIndex(newIndex);
    } else {
      setImgIndex(newIndex);
    }
  }
